tutorialsIndex_pt = {"Básico", "Intermediário", "Avançado", "Sub-classes", "Receitas - Alquimia", "Receitas - Forja"}

tutorialsInfo_pt = {
    {
        {name = "01. Introdução", text = "Icone 1: Representa a Janela de Batalha (onde encontrarão criaturas e players por perto).\n\Icone 2: Representa suas skills.\n\Icone 3: Representa as informações de vida e energia.\n\Icone 4: Representa seu inventário.\n\Icone 5: Representa o mapa.\n\Icone 6: Representa sua lista de amigos.\n\Icone 7: Representa suas dobras(magias).\n\Icone 8: Representa utilidades extras do jogo.\n\Icone 9: Representa a janela de atributos disponíveis.", img = "imgs/b1"}, 
        {name = "02. Manuseando Itens", text = "Para manusear itens, apenas clique e segure com o botão esquerdo do mouse e arraste-o para onde quiser, você pode mover itens de dentro ou fora de seu inventário, e também coletá-los ou joga-los fora.\n\nEntretanto existem itens únicos que não podem ser descartados.", img = "imgs/b2"}, 
        {name = "03. Atacando inimigos", text = "Para atacar um inimigo, apenas clique em seu alvo (botão direito do mouse), depois que o alvo for marcado, você vai atacar o inimigo automaticamente.", img = "imgs/b3"}, 
        {name = "04. Usando ataques", text = "Seus ataques são desbloqueados conforme você sobe de level. Para usar um ataque apenas clique em seu ícone (botão esquerdo do mouse) ou utilize os atalhos 'F1, F2, F3, F4, ...'. Os atalhos também podem ser redefinidos nas configurações de Hotkeys.", img = "imgs/b4"}, 
        {name = "05. Loot das criaturas", text = "Toda criatura depois de derrotado pode conter itens, para coletá-los, apenas clique na criatura caida (botão direito do mouse).", img = "imgs/b5"}, 
        {name = "06. Restaurando vida/energia", text = "Para recuperar sua vida, basta comer algum tipo de comida, que pode ser dropada nas criaturas. Outra maneira é usando poção de vida (como mostra a figura).\n\Para usar poções, clique com o botão direito em cima dela e depois clique com o esquerdo em cima de você ou jogador que queira usar a potion. Outra maneira é usando Hotkeys.", img = "imgs/b6"}, 
        {name = "07. Mapa", text = "O mapa do jogo pode ser aberto com o atalho 'Ctrl+M' ou por seu ícone.\n\nO mapa mostra localizações importantes como cidades e lojas.\n\nPara visualizar o mapa por inteiro, use 'Ctrl+Shift+M'.", img = "imgs/b7"}, 
		{name = "08. Mercado", text = "Para abrir o inventário de uma loja, incie uma conversação com o e npc diga:\n\n-Hi.\n\n-Trade.", img = "imgs/b8"}, 
        {name = "09. Depósito", text = "O depósito pode ser encontrado em qualquer Centro de depósitos, para abrir, apenas clique no depósito (botão direito do mouse). No depósito você pode guardar quantos itens quiser.", img = "imgs/b9"}
    },
    {
        {name = "01. Olhar itens", text = "Para olhar algo, posicione o mouse e pressione os 2 botões ao mesmo tempo.\n\nOutra maneira de olhar algo é utilizando 'Ctrl'+botão direito do mouse e selecionando a opção 'Olhar'.\n\nOlhar é muito útil para pegar informações de itens, principalmente para saber o nome deles.", img = "imgs/i1"},
        {name = "02. Controles de combate", text = "Controles de combate são muito importantes, principalmente para a caça.\n\nOfensivo: Ataque extra, porém um pouco da defesa é perdida.\n\nBalanceado: Força balanceada\n\nDefensivo: Defesa extra porém um pouco do ataque perdido.\n\nFollow attack: Essa opção permite que você siga automaticamente a criatura que está atacando.\n\nPvP: Habilitando essa opção você vai poder atacar outras pessoas, porém o PvP só é permitido depois de um certo Level.", img = "imgs/i2"},
        {name = "03. Hotkeys", text = "Aqui você pode adicionar novos atalhos e até colocar itens como potions, ou comandos de batalha como ataques etc.", img = "imgs/i3"},
        {name = "04. Aceitando Quests", text = "Para aceitar quests básicas é muito simples, comece a conversa com o NPC com a palavra 'hi', depois ofereça ajuda ou peça uma missão para o NPC com 'help' ou 'mission', então aceite a quest com 'yes'.\n\nQuando terminar a tarefa pedida, volte no NPC e repita o mesmo processo.\n\nQuests mais complexas exigem sua própria interpretação para descobrir o diálogo envolvido.", img = "imgs/i4"},
        {name = "05. Lista de amigos", text = "A lista de amigos mostra se seus amigos estão Online ou Offline no jogo, além de fornecer várias opções como mensagens privadas ou ignorar.\n\nPara adicionar um amigo é muito fácil, apenas clique com o botão direito na lista, selecione a opção de adicionar e digite o nome do jogador que deseja adicionar.\n\nOutro meio de adicionar alguém, é segurando 'Ctrl' e clicando no jogador que deseja adicionar, depois selecionar a opção.", img = "imgs/i5"},
    },	
    {
        {name = "01. Trade", text = "Para fazer uma troca, segure 'Ctrl' e clique com o botão direito do mouse no item que deseja trocar e selecione 'Trocar com', depois use no jogador que você deseja fazer a troca. (no caso de vários itens, coloque-os em uma bag e troque a bag).", img = "imgs/a1"},
        {name = "02. Casas", text = "Para comprar uma casa, você precisa ter VIP account e pelo menos level 30.\n\Primeiramente encontre uma casa sem dono, 'olhe' a porta da casa para verificar e saber o preço, depois fique de frente para a porta da casa e digite !buyhouse.\n\Todo mês será debitado o preço da casa do seu depósito, caso contrário, você perderá sua casa (os itens são enviados para o depósito automaticamente). Para ver informações sobre o aluguel digite !houseinfo.\n\O dono da casa possui vários comandos:\n\!invite - Para convidar alguém para a sua casa.\n\!subowner - Para definir um segundo dono para a casa.\n\!door - Para definir quem pode abrir determinada porta (fique de frente para a porta em questão).\n\!kick player - Para jogar a pessoa para fora de sua casa.\n\!sellhouse player - Para vender a casa para algum jogador.\n\!leavehouse Para abandonar a casa.", img = "imgs/a9"},
        {name = "03. Party e Channels", text = "1. Private Chat Channel (para criar um canal particular e convidar jogadores)\n2. Game-Chat - Canal para conversas.\n3. Trade - Canal de comércio, para compra e venda de items.\n4. Help - Canal de ajuda, para perguntas a tutores.", img = "imgs/a10"},
        {name = "04. Mineração", text = "Para minerar uma rocha, clique com o botão direito do mouse em sua pick e depois com o botão esquerdo, clique nas rochas próprias para o mineração (semelhante ou igual a imagem acima).", img = "imgs/a3"},
        {name = "05. Refinamento", text = "Você pode refinar os minérios e transforma-los em barras, as barras são necessárias para serem utilizadas na forja, alguns minérios como estanho e cobre precisam ser refinados juntos para formar o bronze, você pode refina-los na sala de forja, para acessa-la fale com o npc ferreiro e utilize as fornalhas que se encontram nas salas de forja.", img = "imgs/a4"},
        {name = "06. Meditação", text = "Todos os jogadores podem meditar, a meditação tem como fundamento melhorar a regeneração do usuário, para utilizá-la basta clicar no icone que se encontra no inventário, durante a meditação, seu personagem não pode tomar danos ou se mover, caso isso aconteça, ela é cancelada.", img = "imgs/meditate1"},
    },	
    {
        {name = "01. Classes", text = "Você pode encontrar os npcs de cada sub-classe ao norte do republic center.", img = "imgs/classes"},
        {name = "02. Forja",           text = "Após encontrar o npc de forja, diga:\n\n-Hi.\n\n-Enter.\n\n-Yes.\n\n- Você será teleportado para a sala de forja, você pode fazer diversos items, cada item tem uma skill necessária de forja e também é necessário colocar todos os materiais do manual em cima da bigorna, e utilizar algum martelo para construir seu novo item.\n\nObs: As receitas estarão na guia , Receitas – Forja.", img = "imgs/p2"},
        {name = "03. Alquimia",               text = "Após encontrar o npc de alquimia, diga:\n\n-Hi.\n\n-Enter.\n\n-Yes.\n\n- Você será teleportado para a sala de alquimia, você pode fazer diversas potions, cada potion tem uma skill necessária de alquimia e necesário colocar os items da receita dentro do caldeirão, após colocar seus items no caldeirão, clique em checar receitas, após isso em fazer.\n\nObs: As receitas estarão na guia , Receitas – Alquimia.", img = "imgs/p3"},
    },	
